import React, { useState, useEffect } from 'react';

const ResultPage = () => {
  const [internshipResults, setInternshipResults] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch data from the server (MongoDB or any other source)
    // Update the 'internshipResults' state with the fetched data
    fetch(`your_api_endpoint/${studentId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Student not found');
        }
        return response.json();
      })
      .then(data => {
        setInternshipResults(data);
        setError('');
      })
      .catch(error => {
        setInternshipResults([]);
        setError('Mã số sinh viên không hợp lệ');
        console.error('Error fetching data:', error);
      });
  }, [studentId]);

  return (
    <div>
      <h2>Kết quả thực tập</h2>
      <div>
        <label>
          Nhập mã số sinh viên: 
          <input
            type="text"
            value={studentId}
            onChange={(e) => setStudentId(e.target.value)}
          />
        </label>
        <button onClick={() => setStudentId('')}>Xóa</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </div>
      <table>
        <thead>
          <tr>
            <th>Sinh viên</th>
            <th>Điểm</th>
            <th>Nhận xét</th>
          </tr>
        </thead>
        <tbody>
          {internshipResults.map((result, index) => (
            <tr key={index}>
              <td>{result.studentName}</td>
              <td>{result.score}</td>
              <td>{result.comment}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ResultPage;
