import React, { useState } from 'react';
import './FormTaiLieu.css';
const FormTaiLieu = ({ onUpload }) => {
  const [tenTaiLieu, setTenTaiLieu] = useState('');
  const [file, setFile] = useState(null);

  const handleTenTaiLieuChange = (e) => {
    setTenTaiLieu(e.target.value);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Xử lý tải lên tài liệu
    if (tenTaiLieu && file) {
      const taiLieu = {
        tenTaiLieu,
        file,
      };
      onUpload(taiLieu);
      // Gửi dữ liệu lên server hoặc xử lý theo nhu cầu của bạn
      console.log('Đã tải lên tài liệu:', taiLieu);
      // Reset trạng thái form
      setTenTaiLieu('');
      setFile(null);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Tên Tài Liệu:
        <input type="text" value={tenTaiLieu} onChange={handleTenTaiLieuChange} />
      </label>
      <label>
        Chọn Tài Liệu:
        <input type="file" onChange={handleFileChange} />
      </label>
      <button type="submit">Tải Lên</button>
    </form>
  );
};

export default FormTaiLieu;
