import React, { useState, useEffect } from 'react';

const ResultPage = () => {
  const [internshipResults, setInternshipResults] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');

  const handleSearch = () => {
    // Kiểm tra xem mã xác nhận có hợp lệ không
    if (isValidVerificationCode(verificationCode)) {
      // Fetch data from the server (MongoDB or any other source) based on the verification code
      // Update the 'internshipResults' state with the fetched data
      fetch(`your_api_endpoint/${verificationCode}`)
        .then(response => response.json())
        .then(data => setInternshipResults(data))
        .catch(error => console.error('Error fetching data:', error));

      // Reset error state
      setError('');
    } else {
      // Nếu mã xác nhận không hợp lệ, hiển thị thông báo lỗi
      setError('Mã xác nhận không hợp lệ');
    }
  };

  const generateRandomCode = () => {
    // Tạo mã xác nhận ngẫu nhiên, có thể thay đổi theo yêu cầu của bạn
    const randomCode = Math.random().toString(36).substring(2, 8);
    setVerificationCode(randomCode);
  };

  // Hàm kiểm tra mã xác nhận có hợp lệ hay không (có thể thay đổi theo yêu cầu của bạn)
  const isValidVerificationCode = (code) => {
    // Thực hiện kiểm tra mã xác nhận ở đây, ví dụ: kiểm tra độ dài, ký tự đặc biệt, ...

    // Đây chỉ là một ví dụ đơn giản, bạn cần thay đổi theo yêu cầu cụ thể của bạn
    return code.length === 6; // Giả sử mã xác nhận có 6 ký tự
  };

  return (
    <div>
      <h2>Kết quả thực tập</h2>

      {/* Hiển thị thông báo lỗi nếu có */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Input mã xác nhận và nút tìm kiếm */}
      <div>
        <label>Mã xác nhận:</label>
        <input
          type="text"
          value={verificationCode}
          onChange={(e) => setVerificationCode(e.target.value)}
        />
        <button onClick={handleSearch}>Tìm kiếm</button>
      </div>

      {/* Nút để tạo và nhập mã xác nhận ngẫu nhiên */}
      <button onClick={generateRandomCode}>Mã Ngẫu Nhiên</button>

      {/* Input mã sinh viên */}
      <div>
        <label>Mã số sinh viên:</label>
        <input
          type="text"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />
        {/* Bạn có thể thêm một nút tìm kiếm cho mã sinh viên nếu cần */}
      </div>

      {/* Hiển thị kết quả nếu có */}
      {internshipResults.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Sinh viên</th>
              <th>Điểm</th>
              <th>Nhận xét</th>
            </tr>
          </thead>
          <tbody>
            {internshipResults.map((result, index) => (
              <tr key={index}>
                <td>{result.studentName}</td>
                <td>{result.score}</td>
                <td>{result.comment}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ResultPage;
