// BaoCaoTienTrinh.js
import React, { useState } from 'react';

const BaoCaoTienTrinh = ({ student }) => {
  const [weeklyReport, setWeeklyReport] = useState('');

  const handleReportChange = (e) => {
    setWeeklyReport(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Gửi báo cáo hàng tuần lên server hoặc xử lý theo nhu cầu của bạn
    console.log(`Báo cáo hàng tuần của ${student.name}: ${weeklyReport}`);
    // Thực hiện các xử lý cập nhật tiến trình thực tập
  };

  return (
    <div>
      <h3>{student.name}</h3>
      <p>Đơn vị thực tập: {student.internship}</p>
      <form onSubmit={handleSubmit}>
        <label>
          Báo cáo hàng tuần:
          <textarea value={weeklyReport} onChange={handleReportChange} />
        </label>
        <button type="submit">Cập nhật</button>
      </form>
    </div>
  );
};

export default BaoCaoTienTrinh;
