// StudentManagementForm.js
import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import './StudentManagementForm.css';

const fakeStudentsData = [
  { id: 1, mssv: '20012005556', name: 'Nguyen Van A', grade: 8.5 },
  { id: 2, mssv: '20012002555', name: 'Tran Thi B', grade: 7.9 },
  // Thêm thông tin sinh viên khác nếu cần
];

const StudentList = ({ students, selectedStudents, onSelectStudent, onExportExcel }) => {
  return (
    <div className="student-list">
      <h2>Danh Sách Sinh Viên</h2>
      <ul>
        {students.map((student) => (
          <li key={student.id} onClick={() => onSelectStudent(student)}>
            <input
              type="checkbox"
              checked={selectedStudents.includes(student.id)}
              onChange={() => {}}
            />
            <span>{student.name}</span> - Điểm: {student.grade}
          </li>
        ))}
      </ul>
      <button onClick={onExportExcel}>Xuất Excel</button>
    </div>
  );
};

const StudentDetail = ({ selectedStudentInfo }) => {
  return (
    <div className="student-detail">
      <h2>Thông Tin Chi Tiết Sinh Viên</h2>
      {selectedStudentInfo && (
        <div>
          <p>MSSV: {selectedStudentInfo.mssv}</p>
          <p>Tên: {selectedStudentInfo.name}</p>
          <p>Điểm: {selectedStudentInfo.grade}</p>
        </div>
      )}
    </div>
  );
};

const StudentManagementForm = () => {
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [students, setStudents] = useState(fakeStudentsData);
  const [selectedStudentInfo, setSelectedStudentInfo] = useState(null);

  const handleSelectStudent = (student) => {
    setSelectedStudentInfo(student);
  };

  const handleExportExcel = () => {
    const data = selectedStudents.map((studentId) => {
      const student = students.find((s) => s.id === studentId);
      return { StudentName: student.name, StudentId: student.id, Grade: student.grade };
    });

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'SelectedStudents');
    XLSX.writeFile(wb, 'selected_students.xlsx');
  };

  return (
    <div className="student-management-form">
      <StudentList
        students={students}
        selectedStudents={selectedStudents}
        onSelectStudent={handleSelectStudent}
        onExportExcel={handleExportExcel}
      />
      <StudentDetail selectedStudentInfo={selectedStudentInfo} />
    </div>
  );
};

export default StudentManagementForm;
