import React, { useState } from 'react';
import * as XLSX from 'xlsx';

const StudentManagementForm = () => {
  // State để lưu danh sách sinh viên đã chọn
  const [selectedStudents, setSelectedStudents] = useState([]);
  const students = /* Dữ liệu sinh viên, có thể fetch từ API hoặc sử dụng state từ component cha */ [];

  // Hàm xử lý chọn/deselect sinh viên
  const handleStudentSelection = (studentId) => {
    const updatedStudents = selectedStudents.includes(studentId)
      ? selectedStudents.filter((id) => id !== studentId)
      : [...selectedStudents, studentId];

    setSelectedStudents(updatedStudents);
  };

  // Hàm xử lý xuất file Excel
  const handleExportExcel = () => {
    const data = selectedStudents.map((studentId) => {
      const student = students.find((s) => s.id === studentId);
      return { StudentName: student.name, StudentId: student.id, /* thêm các trường thông tin khác nếu cần */ };
    });

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'SelectedStudents');
    XLSX.writeFile(wb, 'selected_students.xlsx');
  };

  return (
    <div className="student-management-container">
    <h2 className="student-management-heading">Quản lý sinh viên</h2>
    <ul className="student-list">
      {students.map((student) => (
        <li key={student.id} className="student-item">
          <input
            type="checkbox"
            checked={selectedStudents.includes(student.id)}
            onChange={() => handleStudentSelection(student.id)}
            className="student-checkbox"
          />
          {student.name} - Điểm: {student.grade}
        </li>
      ))}
    </ul>
    <button onClick={handleExportExcel} className="export-button">Xuất Excel</button>
  </div>
  );
};

export default StudentManagementForm;
