import React, { useState, useEffect } from 'react';

const ResultPage = () => {
  const [internshipResults, setInternshipResults] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch data when the component mounts (you can modify this based on your requirements)
    handleSearch();
  }, []);

  const handleSearch = () => {
    // Kiểm tra xem mã số sinh viên có hợp lệ không
    if (isValidStudentId(studentId)) {
      // Fetch data from the server (MongoDB or any other source) based on the student ID
      // Update the 'internshipResults' state with the fetched data
      fetch(`your_api_endpoint/${studentId}`)
        .then(response => response.json())
        .then(data => setInternshipResults(data))
        .catch(error => console.error('Error fetching data:', error));
      
      // Reset error state
      setError('');
    } else {
      // Nếu mã số sinh viên không hợp lệ, hiển thị thông báo lỗi
      setError('Mã số sinh viên không hợp lệ');
    }
  };

  const generateRandomId = () => {
    // Tạo một mã số sinh viên ngẫu nhiên, có thể thay đổi theo yêu cầu của bạn
    const randomId = Math.floor(Math.random() * 90000000) + 10000000;
    setStudentId(randomId.toString());
  };

  // Hàm kiểm tra mã số sinh viên có hợp lệ hay không (có thể thay đổi theo yêu cầu của bạn)
  const isValidStudentId = (studentId) => {
    // Thực hiện kiểm tra mã số sinh viên ở đây, ví dụ: kiểm tra độ dài, ký tự đặc biệt, ...

    // Đây chỉ là một ví dụ đơn giản, bạn cần thay đổi theo yêu cầu cụ thể của bạn
    return studentId.length === 8 && /^\d+$/.test(studentId);
  };

  return (
    <div>
      <h2>Kết quả thực tập</h2>
      
      {/* Hiển thị thông báo lỗi nếu có */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Input mã số sinh viên và nút tìm kiếm */}
      <div>
        <label>Mã số sinh viên:</label>
        <input
          type="text"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />
        <button onClick={handleSearch}>Tìm kiếm</button>
      </div>

      {/* Nút để tạo và nhập mã ngẫu nhiên */}
      <button onClick={generateRandomId}>Mã Ngẫu Nhiên</button>

      {/* Hiển thị kết quả nếu có */}
      {internshipResults.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Sinh viên</th>
              <th>Điểm</th>
              <th>Nhận xét</th>
            </tr>
          </thead>
          <tbody>
            {internshipResults.map((result, index) => (
              <tr key={index}>
                <td>{result.studentName}</td>
                <td>{result.score}</td>
                <td>{result.comment}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ResultPage;
