import React, { useState } from 'react';
import './ResultPage.css';


const ResultPage = () => {
  const [internshipResults, setInternshipResults] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [error, setError] = useState('');

  const handleSearch = () => {
    // Kiểm tra xem mã số sinh viên có hợp lệ không
    if (isValidStudentId(studentId)) {
      // Giả định dữ liệu mẫu cho hai sinh viên
      const sampleData = [
        { studentId: '2001200216', studentName: 'John Doe', score: 8.5, comment: 'Good performance' },
        { studentId: '2001200218', studentName: 'Jane Doe', score: 9.2, comment: 'Excellent work' }
      ];

      // Lọc sinh viên theo mã số sinh viên
      const selectedStudent = sampleData.find(student => student.studentId === studentId);

      if (selectedStudent) {
        // Nếu có sinh viên, cập nhật kết quả thực tập
        setInternshipResults([selectedStudent]);
        // Reset error state
        setError('');
      } else {
        // Nếu không tìm thấy sinh viên, hiển thị thông báo lỗi
        setError('Không tìm thấy sinh viên');
      }
    } else {
      // Nếu mã số sinh viên không hợp lệ, hiển thị thông báo lỗi
      setError('Mã số sinh viên không hợp lệ');
    }
  };

  // Hàm kiểm tra mã số sinh viên có hợp lệ hay không
  const isValidStudentId = (studentId) => {
    // Thực hiện kiểm tra mã số sinh viên ở đây, ví dụ: kiểm tra độ dài, ký tự đặc biệt, ...

    // Đây chỉ là một ví dụ đơn giản, bạn cần thay đổi theo yêu cầu cụ thể của bạn
    return studentId.length === 10 && /^\d+$/.test(studentId);
  };

  return (
    <div>
      <h2>KẾT QUẢ THỰC TẬP</h2>

      {/* Hiển thị thông báo lỗi nếu có */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Input mã số sinh viên và nút tìm kiếm */}
      <div className="search-container">
        <div className="label-input-container">
            <label>Mã Số Sinh Viên:</label>
            <input
            type="text"
            value={studentId}
            onChange={(e) => setStudentId(e.target.value)}
            />
        </div>
        <button onClick={handleSearch}>Tìm kiếm</button>
        </div>

      {internshipResults.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Sinh viên</th>
              <th>Điểm</th>
              <th>Nhận xét</th>
            </tr>
          </thead>
          <tbody>
            {internshipResults.map((result, index) => (
              <tr key={index}>
                <td>{result.studentName}</td>
                <td>{result.score}</td>
                <td>{result.comment}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ResultPage;
