import React, { useState, useEffect } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';

const ResultPage = () => {
  const [internshipResults, setInternshipResults] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [captchaValue, setCaptchaValue] = useState('');
  const [error, setError] = useState('');

  const handleSearch = () => {
    // Kiểm tra xem mã số sinh viên và mã xác nhận có hợp lệ không
    if (
      isValidStudentId(studentId) &&
      isValidVerificationCode(verificationCode) &&
      isValidCaptchaValue(captchaValue)
    ) {
      // Fetch data from the server (MongoDB or any other source) based on the student ID
      // Update the 'internshipResults' state with the fetched data
      fetch(`your_api_endpoint/${studentId}`)
        .then(response => response.json())
        .then(data => setInternshipResults(data))
        .catch(error => console.error('Error fetching data:', error));

      // Reset error state
      setError('');
    } else {
      // Nếu thông tin nhập vào không hợp lệ, hiển thị thông báo lỗi
      setError('Thông tin nhập không hợp lệ');
    }
  };

  const generateRandomCode = () => {
    // Tạo mã xác nhận ngẫu nhiên, có thể thay đổi theo yêu cầu của bạn
    const randomCode = Math.random().toString(36).substring(2, 8);
    setVerificationCode(randomCode);
  };

  // Hàm kiểm tra mã số sinh viên có hợp lệ hay không
  const isValidStudentId = (studentId) => {
    return studentId.length === 8 && /^\d+$/.test(studentId);
  };

  // Hàm kiểm tra mã xác nhận có hợp lệ hay không
  const isValidVerificationCode = (code) => {
    return code.length === 6; // Giả sử mã xác nhận có 6 ký tự
  };

  // Hàm kiểm tra giá trị mã bảo vệ có hợp lệ hay không
  const isValidCaptchaValue = (captchaValue) => {
    return captchaValue !== '';
  };

  const handleCaptchaChange = (value) => {
    // Lưu giá trị captcha khi thay đổi
    setCaptchaValue(value);
  };

  return (
    <div>
      <h2>Kết quả thực tập</h2>

      {/* Hiển thị thông báo lỗi nếu có */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Input mã xác nhận và nút tìm kiếm */}
      <div>
        <label>Mã xác nhận:</label>
        <input
          type="text"
          value={verificationCode}
          onChange={(e) => setVerificationCode(e.target.value)}
        />
        <button onClick={handleSearch}>Tìm kiếm</button>
      </div>

      {/* Nút để tạo và nhập mã xác nhận ngẫu nhiên */}
      <button onClick={generateRandomCode}>Mã Ngẫu Nhiên</button>

      {/* Input mã sinh viên */}
      <div>
        <label>Mã số sinh viên:</label>
        <input
          type="text"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />
        {/* Bạn có thể thêm một nút tìm kiếm cho mã sinh viên nếu cần */}
      </div>

      {/* Hiển thị hình ảnh mã bảo vệ */}
      <ReCAPTCHA
        sitekey="6LfZ1iQpAAAAAHNZB-NI1BRrYYoiFbJgaDW1t1-z" // Thay YOUR_SITE_KEY bằng khóa site của bạn từ Google ReCAPTCHA
        onChange={handleCaptchaChange}
      />

      {/* Hiển thị kết quả nếu có */}
      {internshipResults.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Sinh viên</th>
              <th>Điểm</th>
              <th>Nhận xét</th>
            </tr>
          </thead>
          <tbody>
            {internshipResults.map((result, index) => (
              <tr key={index}>
                <td>{result.studentName}</td>
                <td>{result.score}</td>
                <td>{result.comment}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ResultPage;
