import React, { useState } from 'react';
import BusinessManager from './BusinessManager';
import InternshipForm from './InternshipForm';

const InternshipPage = () => {
  const [registeredInternships, setRegisteredInternships] = useState([]);

  const handleRegisterInternship = (internshipData) => {
    setRegisteredInternships([...registeredInternships, internshipData]);
  };

  return (
    <div>
      <BusinessManager />
      <InternshipForm
        businesses={[]} // Pass the businesses array here
        onRegister={handleRegisterInternship}
      />

      <div className="registered-internships">
        <h2>Đã đăng ký thực tập</h2>
        <ul>
          {registeredInternships.map((internship, index) => (
            <li key={index}>
              <strong>Doanh nghiệp:</strong> {internship.businessId},{' '}
              <strong>Sinh viên:</strong> {internship.studentName},{' '}
              <strong>Email:</strong> {internship.studentEmail}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default InternshipPage;
