// InternshipResultForm.js
import React, { useState } from 'react';
import './InternshipResultForm.css';

const InternshipResultForm = ({ internshipResults }) => {
  const [searchInput, setSearchInput] = useState('');
  const [searchedResult, setSearchedResult] = useState(null);

  const searchResult = () => {
    // Tìm kết quả thực tập của sinh viên
    const result = internshipResults.find(item => item.studentId === searchInput);

    // Cập nhật state để hiển thị kết quả
    setSearchedResult(result);
  };

  return (
    <div className="internship-result-form">
      <h2>Kết Quả Thực Tập Sinh Viên</h2>
      <div className="search-container">
        <input
          type="text"
          placeholder="Nhập Mã SV..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={searchResult}>Tìm Kiếm</button>
      </div>
      <div className="result-container">
        {searchedResult ? (
          <div className="result-item">
            <p><strong>Mã số sinh viên:</strong> {searchedResult.studentId}</p>
            <p><strong>Họ và tên sinh viên:</strong> {searchedResult.studentName}</p>
            <p><strong>Kết quả:</strong> {searchedResult.result}</p>
            <p><strong>Ghi chú:</strong> {searchedResult.note}</p>
          </div>
        ) : (
          <p className="no-result-message">Không tìm thấy kết quả thực tập cho mã số sinh viên này.</p>
        )}
      </div>
    </div>
  );
};

export default InternshipResultForm;
